public abstract class BaseViewModel : INotifyPropertyChanged, IInitialize, INavigatedAware, IPageLifecycleAware
{
        protected INavigationService NavigationService { get; }
        protected IPageDialogService PageDialogService { get; }
        public bool IsBusy { get; set; }
        public string Title { get; set; }

        public BaseViewModel(INavigationService navigationService, IPageDialogService pageDialogService)
        {
            NavigationService = navigationService;
            PageDialogService = pageDialogService;
        }

        public virtual void Initialize(INavigationParameters parameters) { }

        public virtual void OnNavigatedFrom(INavigationParameters parameters) { }

        public virtual void OnNavigatedTo(INavigationParameters parameters) { }

        public virtual void OnAppearing() { }

        public virtual void OnDisappearing() { }

        public event PropertyChangedEventHandler PropertyChanged;
}
$end$
